import React, { useState } from "react";
import Header from "./components/Header";
import "./App.css";
import TaskList from "./components/TaskList";

function App() {
  const [tasks, setTasks] = useState([
    { id: "task1", title: "Learn JS fundamental", status: 0 },
    { id: "task1", title: "Code a Todo List level 2", status: 1 },
  ]);
  const [showIncomplete, setShowIncomplete] = useState(true);

  const [newTask, setNewTask] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newTask) {
      const task = {
        id: Date.now(),
        title: newTask,
        status: 0,
      };
      setTasks([...tasks, task]);
      setNewTask("");
    }
  };

  const handleInputChange = (e) => {
    setNewTask(e.target.value);
  };

  const setTaskStatus = (taskId, status) => {
    setTasks(tasks.map(task => {
      if (task.id === taskId) {
        return {...task, status: status ? 1 : 0}
      }
      return task;
    }))
  }

  const removeTask =(taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId))
  }

  return (
    <div className="container">
      <Header title="Todo List" subTitle="Get things done"/>
      <TaskList tasks={tasks} showIncomplete={showIncomplete} setTaskStatus={setTaskStatus} removeTask={removeTask} setShowIncomplete={setShowIncomplete}/>
      <form onSubmit={handleSubmit} className="form">
        <label htmlFor="newitem">Add to the todo list</label>
        <input
          type="text"
          id="newitem"
          value={newTask}
          onChange={handleInputChange}
        ></input>
        <button type="submit">Add Item</button>
      </form>
    </div>
  );
}

export default App;
