import React, { useState } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([
    {id: "task1", title:"Learn JS fundamental", status: 0},
    {id: "task1", title:"Code a Todo List level 2", status: 0},
  ]);
  const [showIncomplete, setShowIncomplete] = useState(false);

  const [newTask, setNewTask] = useState("");

  return (
  <div className="container">
    <h1 id="title">Todo List
      <span className="title-span">Get one item done at a time.</span>
    </h1>
    <ul className="task-list">
      {tasks.map(task => (
        <li key={task.id} className={task.status ? "done" : ""}>
        <span className="label">{task.title}</span>
        <div className="actions">
          <input type="checkbox" className="btn-action btn-action-done"></input>
          <button className="btn-action btn-action-delete">âœ–</button>
        </div>
      </li>
      ))}
    </ul>
    <div className="filter-wrapper">
      <label htmlFor="filter" className="filter-lable">Show incompleted tasks only</label>
      <input type="checkbox" id="filter"></input>
    </div>
    <form action="#" className="form">
      <label htmlFor="newitem">Add to the todo list</label>
      <input type="text" id="newitem"></input>
      <button type="submit">Add Item</button>
    </form>
  </div>   
  );
}

export default App;
